regexsel.eval:
if (selstart == 0 && selend == len(valstem)) {
	restoreext();
	if (len(valstem) < len(value)) {
		selstart = len(valstem) + 1;
		selend = len(value);
	}
}
elseif (selstart == (len(valstem) + 1) && selend == len(value)) {
	selstart = 0;
	selend = len(value);
}
else {
	selstart = 0;
	selend = len(valstem);
}
